mapper.select:
SELECT * FROM question_anwsers
 WHERE parent_id = 0

    AND dataset_id=#{dataset_id}




     AND content  like #{content}

AND deleted_date IS  NULL
ORDER BY updated_date DESC , id DESC

SELECT count(qa.id) FROM question_anwsers qa, data_sets ds WHERE qa.type=1 and ds.id=qa.dataset_id AND parent_id=0 AND deleted_date IS NULL
AND content like concat('%',#{search_str},'%')
AND ds.vendor_id=#{vendor_id}
AND qa.parent_id=#{questionId}

SELECT count(qa.id) FROM question_anwsers qa, data_sets ds WHERE qa.type=2 and ds.id=qa.dataset_id AND qa.deleted_date IS NULL
AND qa.content like concat('%',#{search_str},'%')
AND ds.vendor_id=#{vendor_id}
AND qa.parent_id=#{questionId}

SELECT qa.*,ds.name as dataset_name FROM question_anwsers qa, data_sets ds
WHERE qa.parent_id=0
AND ds.id=qa.dataset_id
AND qa.deleted_date IS  NULL

    AND  qa.content like concat('%',#{search_str},'%')


    AND ds.vendor_id=#{vendor_id}


    AND qa.parent_id=#{questionId}

ORDER BY updated_date DESC
limit #{start},#{limit}

SELECT qa.* FROM question_anwsers qa, data_sets ds
WHERE qa.type=2
and ds.id=qa.dataset_id
AND qa.deleted_date IS  NULL

   AND qa.content like concat('%',#{search_str},'%')


    AND ds.vendor_id=#{vendor_id}


    AND qa.parent_id=#{questionId}

limit #{start},#{limit}

mapper.update:
update question_anwsers
set   content = #{content}
    ,dataset_id = #{dataset_id}
    ,question_by_userid = #{question_by_userid}
    ,type = #{type}
    ,parent_id = #{parent_id}
    ,votes = #{votes}
    ,answer_by = #{answer_by}
    ,deleted_date = #{deleted_date}
    ,updated_date = #{updated_date}
where id=#{id}

update question_anwsers
set votes=#{votes}
where id=#{questionItemId}

