mapper.update:
update user set
  password=#{password}
where id =#{id}

update user set
username = #{username}
,email=#{email}
,membership=#{membership}
,up_membership=#{up_membership}
,balance=#{balance}
,expire_date=#{expire_date}
, apiKey=#{apiKey}
, customer_id=#{customer_id}
, vendor_id=#{vendor_id}
where id =#{id}

update user set
activate = 1
where id =#{id}

update user set
activate = 0
where id =#{id}

update user set
customer_id=#{customer_id}
where vendor_id=#{vendor_id}

UPDATE user SET
balance = balance + #{balance}
WHERE vendor_id = #{vendor_id}
